$(function() {
	
	$.widget('ui.slider', $.ui.slider, {
    _init: function() {
        //this.element.children().css('position', 'relative'); //for example
    },
    options : {
        slidestart: function( event, ui ) {
			$(this.element).find('.ui-slider-handle.ui-state-hover')
			.append('<span class="ui-slider-tooltip">'+ui.value+'</span>');
		},
		slide: function( event, ui ) {
			$(this.element).find('.ui-slider-tooltip').text(ui.value);
		},
		slidestop: function( event, ui ) {
			$(this.element).find('.ui-slider-tooltip').remove();
		}
    }
});
	
});